package class_01;import java.util.ArrayList;import java.util.List;/*    * 배열 사용의 한계     - 데이터가 변경될 때 각각의 배열마다 인덱스 순서를 정확하게 찾아서 제거해줘야 한다. */public class Class_02 {    public static void main(String[] args) {        List<Student> students = new ArrayList<>();        students.add(createStudent("Song", 34, 90));        students.add(createStudent("Lee", 33, 80));        students.forEach(student -> System.out.println("Name : " + student.name + "\n" + "Age : " + student.age + "\n" + "Grade : " + student.grade));    }    private static Student createStudent(String name, int age, int grade) {        Student student = new Student();        student.name = name;        student.age = age;        student.grade = grade;        return student;    }}